import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = ({ selectedLanguage }) => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTopic, setSelectedTopic] = useState('Technology'); // Default topic

  const API_KEY = 'YOUR_GNEWS_API_KEY'; // Replace with your GNews API key

  const fetchNews = async (topic) => {
    try {
      setLoading(true);
      let url;

      // Map sidebar topics to appropriate categories for the API
      const topicToCategory = {
        'Technology': 'technology',
        'Politics': 'politics',
        'Economy': 'business',
        'Health': 'health',
        'Environment': 'science',
        'Uttar Pradesh': 'in',
        'Maharashtra': 'in',
        'Delhi': 'in',
        'Karnataka': 'in',
        'Tamil Nadu': 'in',
      };

      if (searchQuery) {
        // If there's a search query, use the GNews API search endpoint
        url = `https://gnews.io/api/v4/search?q=${searchQuery}&lang=${selectedLanguage}&token=${API_KEY}`;
      } else {
        // For topics, use the GNews API top headlines endpoint
        const category = topicToCategory[topic] || 'general';
        url = `https://gnews.io/api/v4/top-headlines?topic=${category}&lang=${selectedLanguage}&token=${API_KEY}`;
      }

      const response = await fetch(url);
      const data = await response.json();

      if (data && data.articles) {
        const processedArticles = data.articles.map(article => ({
          title: article.title || 'No Title',
          description: article.description || 'No description available',
          url: article.url || '#',
          urlToImage: article.image || 'https://via.placeholder.com/150',
          publishedAt: article.publishedAt || new Date().toISOString(),
          source: {
            name: article.source || 'Unknown Source'
          }
        }));
        setNews(processedArticles);
      } else {
        console.error('Error in API response:', data.message);
        setNews([]);
      }
    } catch (error) {
      console.error('Failed to fetch news:', error);
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (searchQuery) {
      fetchNews(searchQuery);
    }
  };

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
    setSearchQuery(''); // Clear search query when topic changes
    fetchNews(topic); // Fetch news for the selected topic
  };

  useEffect(() => {
    fetchNews(selectedTopic);
  }, [selectedTopic, selectedLanguage]);

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News</title>
        <meta name="description" content="Stay updated with the latest news." />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={handleTopicSelect}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>Latest News - {selectedTopic}</h1>
              <div className="search-container">
                <input 
                  type="text" 
                  placeholder="Search for news..." 
                  value={searchQuery} 
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                />
                <button onClick={handleSearch}>Search</button>
              </div>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : news.length > 0 ? (
              <NewsGrid articles={news} />
            ) : (
              <div className="no-articles">No articles available. Please try a different topic or search term.</div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
