import React from 'react';
import NewsCard from './NewsCard';
import './NewsGrid.css';

const NewsGrid = ({ articles }) => {
  return (
    <div className="news-grid">
      {articles.map(article => (
        <NewsCard 
          key={article._id}
          article={article}
        />
      ))}
    </div>
  );
};

export default NewsGrid;
