import React, { useState, useEffect } from 'react';
import NewsGrid from './NewsGrid';
import AnalyticsSidebar from './AnalyticsSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeCategory, setActiveCategory] = useState('all');
  const [language, setLanguage] = useState('en');
  const [showAnalytics, setShowAnalytics] = useState(false);

  const categories = [
    'all', 'politics', 'technology', 'sports', 'business'
  ];

  useEffect(() => {
    fetchNews();
  }, [activeCategory, language]);

  const fetchNews = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/news?category=${activeCategory}&lang=${language}`);
      const data = await response.json();
      setNews(data);
    } catch (error) {
      console.error('Error fetching news:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>AI News Aggregator</h1>
        <div className="controls">
          <div className="category-filters">
            {categories.map(category => (
              <button
                key={category}
                className={`category-btn ${activeCategory === category ? 'active' : ''}`}
                onClick={() => setActiveCategory(category)}
              >
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </button>
            ))}
          </div>
          <div className="right-controls">
            <select
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
              className="language-select"
            >
              <option value="en">English</option>
              <option value="hi">Hindi</option>
            </select>
            <button 
              className={`analytics-toggle ${showAnalytics ? 'active' : ''}`}
              onClick={() => setShowAnalytics(!showAnalytics)}
            >
              Analytics
            </button>
          </div>
        </div>
      </div>

      <div className={`dashboard-content ${showAnalytics ? 'with-analytics' : ''}`}>
        <div className="main-content">
          {loading ? (
            <div className="loader">Loading...</div>
          ) : (
            <NewsGrid articles={news} />
          )}
        </div>
        {showAnalytics && <AnalyticsSidebar articles={news} />}
      </div>
    </div>
  );
};

export default Dashboard;
