import React, { useState } from 'react';
import './TopicSidebar.css';

const topics = {
  global: {
    name: 'Global News',
    subtopics: ['Politics', 'Technology', 'Economy']
  },
  india: {
    name: 'Indian News',
    subtopics: ['Uttar Pradesh', 'Maharashtra', 'Delhi']
  },
  technology: {
    name: 'Technology',
    subtopics: ['AI', 'Blockchain', 'Gadgets']
  },
  health: {
    name: 'Health',
    subtopics: ['Nutrition', 'Fitness', 'Mental Health']
  },
  // Add more topics and subtopics as needed
};

const TopicSidebar = ({ onTopicSelect, selectedTopic }) => {
  const [activeTopic, setActiveTopic] = useState(null);

  const handleTopicClick = (topic) => {
    setActiveTopic(activeTopic === topic ? null : topic); // Toggle subtopics
  };

  return (
    <div className="topic-sidebar">
      <h2>Select a Topic</h2>
      <ul>
        {Object.entries(topics).map(([key, topic]) => (
          <li key={key}>
            <div
              className={`topic-header ${selectedTopic === key ? 'active' : ''}`}
              onClick={() => {
                handleTopicClick(key);
                onTopicSelect(key); // Select the broader topic
              }}
            >
              {topic.name}
            </div>
            {activeTopic === key && (
              <ul className="subtopics">
                {topic.subtopics.map((subtopic) => (
                  <li
                    key={subtopic}
                    className="subtopic-btn"
                    onClick={() => onTopicSelect(`${key}-${subtopic.toLowerCase()}`)} // Pass the subtopic
                  >
                    {subtopic}
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TopicSidebar;
