import React from 'react';
import { FaGlobe, FaCity, FaLandmark, FaIndustry } from 'react-icons/fa';
import './TopicSidebar.css';

const TopicSidebar = ({ onTopicSelect, selectedTopic }) => {
  const topics = {
    global: {
      name: 'International News',
      icon: <FaGlobe />,
      subtopics: [
        { id: 'politics', name: 'Politics', icon: <FaLandmark /> },
        { id: 'technology', name: 'Technology', icon: <FaIndustry /> },
        { id: 'economy', name: 'Economy', icon: <FaIndustry /> }
      ]
    },
    india: {
      name: 'Indian News',
      icon: <FaCity />,
      subtopics: [
        { id: 'uttarpradesh', name: 'Uttar Pradesh', icon: <FaLandmark /> },
        { id: 'lucknow', name: 'Lucknow', icon: <FaCity /> },
        { id: 'business', name: 'Local Business', icon: <FaIndustry /> }
      ]
    }
  };

  return (
    <div className="topic-sidebar">
      {Object.entries(topics).map(([key, topic]) => (
        <div key={key} className="topic-section">
          <div 
            className={`topic-header ${selectedTopic.startsWith(key) ? 'active' : ''}`}
            onClick={() => onTopicSelect(`${key}-${topic.subtopics[0].id}`)}
          >
            {topic.icon}
            <span>{topic.name}</span>
          </div>
          <div className="subtopics">
            {topic.subtopics.map(subtopic => (
              <button 
                key={subtopic.id}
                className={`subtopic-btn ${selectedTopic === `${key}-${subtopic.id}` ? 'active' : ''}`}
                onClick={() => onTopicSelect(`${key}-${subtopic.id}`)}
              >
                {subtopic.icon}
                <span>{subtopic.name}</span>
              </button>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default TopicSidebar;
