import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = ({ selectedLanguage }) => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTopic, setSelectedTopic] = useState('technology');

  const API_KEY = 'ea90788d06fe4d1c980f73522c31bab7';

  const fetchNews = async (query) => {
    try {
      setLoading(true);
      let url;

      // Map languages to their corresponding countries
      const countryMap = {
        'en': 'us',
        'es': 'es',
        'fr': 'fr',
        'de': 'de',
        'hi': 'in'
      };

      const country = countryMap[selectedLanguage] || 'us';

      if (searchQuery) {
        // For search queries, use the 'everything' endpoint
        url = `https://newsapi.org/v2/everything?q=${query}&language=${selectedLanguage}&sortBy=publishedAt&apiKey=${API_KEY}`;
      } else {
        // For topics, use 'top-headlines' with appropriate country and language
        url = `https://newsapi.org/v2/top-headlines?country=${country}&category=${selectedTopic.toLowerCase()}&apiKey=${API_KEY}`;
      }

      const response = await fetch(url);
      const data = await response.json();

      if (data.status === 'ok' && data.articles) {
        const processedArticles = data.articles.map(article => ({
          title: article.title || 'No Title',
          description: article.description || 'No description available',
          url: article.url || '#',
          urlToImage: article.urlToImage || 'https://via.placeholder.com/150',
          publishedAt: article.publishedAt || new Date().toISOString(),
          source: {
            name: article.source?.name || 'Unknown Source'
          }
        }));
        setNews(processedArticles);
      } else {
        console.error('Error in API response:', data.message);
        setNews([]);
      }
    } catch (error) {
      console.error('Failed to fetch news:', error);
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (searchQuery) {
      fetchNews(searchQuery);
    }
  };

  // Handle topic selection
  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
    setSearchQuery(''); // Clear search query when topic changes
    fetchNews(topic);
  };

  useEffect(() => {
    fetchNews(selectedTopic);
  }, [selectedTopic, selectedLanguage]);

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News</title>
        <meta name="description" content="Stay updated with the latest news." />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={handleTopicSelect}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>Latest News</h1>
              <div className="search-container">
                <input 
                  type="text" 
                  placeholder="Search for news..." 
                  value={searchQuery} 
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                />
                <button onClick={handleSearch}>Search</button>
              </div>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : news.length > 0 ? (
              <NewsGrid articles={news} />
            ) : (
              <div className="no-articles">No articles available. Please try a different topic or search term.</div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
