import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState('general');

  useEffect(() => {
    const fetchNews = async () => {
      try {
        setLoading(true);
        // Using 'everything' endpoint with a more specific query
        const response = await fetch(
          `https://newsapi.org/v2/everything?` + 
          `q=${selectedTopic}&` +
          `sortBy=publishedAt&` +
          `language=en&` +
          `apiKey=ea90788d06fe4d1c980f73522c31bab7`
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();

        // Transform the articles to ensure all required fields are present
        const processedArticles = data.articles.map(article => ({
          title: article.title || 'No Title',
          description: article.description || 'No description available',
          url: article.url || '#',
          urlToImage: article.urlToImage || 'https://via.placeholder.com/150',
          publishedAt: article.publishedAt || new Date().toISOString(),
          source: {
            name: article.source?.name || 'Unknown Source'
          }
        })).filter(article => article.title !== '[Removed]');

        setNews(processedArticles);
      } catch (error) {
        console.error('Error fetching news:', error);
        // Provide some fallback data if the API fails
        setNews([
          {
            title: "Unable to fetch latest news",
            description: "Please try again later. Our news service is temporarily unavailable.",
            url: "#",
            urlToImage: "https://via.placeholder.com/150",
            publishedAt: new Date().toISOString(),
            source: { name: "System" }
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [selectedTopic]);

  return (
    <>
      <Helmet>
        <title>News App</title>
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>Latest News</h1>
              <h2>{selectedTopic.charAt(0).toUpperCase() + selectedTopic.slice(1)}</h2>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : (
              <NewsGrid articles={news} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
