import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import api from '../services/api';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState('global-politics');
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const fetchNews = async () => {
      try {
        setLoading(true);
        setError(null);
        console.log('Fetching news for topic:', selectedTopic);

        // Test the server health first
        const healthCheck = await api.get('/health');
        console.log('Server health:', healthCheck.data);

        const response = await api.get('/api/news', {
          params: {
            topic: selectedTopic,
            language: language
          }
        });

        console.log('Received news data:', response.data);
        setNews(response.data);
      } catch (error) {
        console.error('Error details:', {
          message: error.message,
          response: error.response?.data,
          status: error.response?.status
        });
        setError(error.response?.data?.error || 'Failed to fetch news articles');
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [selectedTopic, language]);

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
    setLoading(true);
  };

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - {selectedTopic.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ')}</title>
        <meta name="description" content={`Latest news on ${selectedTopic.replace('-', ' ')}`} />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={handleTopicSelect}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>
                {selectedTopic.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ')} News
              </h1>
              <select
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
                className="language-select"
              >
                <option value="en">English</option>
                <option value="hi">हिंदी</option>
              </select>
            </div>

            {loading ? (
              <div className="loader">Loading articles...</div>
            ) : error ? (
              <div className="error-message">{error}</div>
            ) : news.length === 0 ? (
              <div className="no-articles">No articles found for this topic.</div>
            ) : (
              <NewsGrid articles={news} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
