import React, { useState } from 'react';
import NewsFetcher from './NewsFetcher'; // Import the new component
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [selectedTopic, setSelectedTopic] = useState('general'); // Default topic
  const [language, setLanguage] = useState('en');

  return (
    <div className="dashboard">
      <TopicSidebar onTopicSelect={setSelectedTopic} selectedTopic={selectedTopic} />
      <NewsFetcher selectedTopic={selectedTopic} language={language} />
    </div>
  );
};

export default Dashboard;
