import React, { useState } from 'react';
import { FaShare, FaBookmark, FaChartLine } from 'react-icons/fa';
import './NewsCard.css';

const NewsCard = ({ article }) => {
  if (!article) {
    return null; // Handle case where article is undefined
  }

  const [expanded, setExpanded] = useState(false);

  const {
    title,
    summary,
    category,
    sentiment,
    confidence,
    source,
    publishedAt,
    translations,
    language
  } = article;

  const getContent = () => {
    if (language === 'hi' && translations?.hi) {
      return {
        title: translations.hi.title,
        summary: translations.hi.summary
      };
    }
    return { title, summary };
  };

  const content = getContent();
  const sentimentColor = {
    positive: '#4CAF50',
    negative: '#f44336',
    neutral: '#9e9e9e'
  }[sentiment];

  return (
    <div className={`news-card ${expanded ? 'expanded' : ''}`}>
      <div className="card-header">
        <span className="category-tag">{category}</span>
        <div className="sentiment-indicator" style={{ backgroundColor: sentimentColor }}>
          {sentiment} ({Math.round(confidence * 100)}%)
        </div>
      </div>

      <h2 className="title">{content.title}</h2>

      <div className="summary">
        <p>
          {expanded ? content.summary : `${content.summary.slice(0, 150)}...`}
        </p>
        <button 
          className="expand-btn"
          onClick={() => setExpanded(!expanded)}
        >
          {expanded ? 'Show Less' : 'Read More'}
        </button>
      </div>

      <div className="card-footer">
        <div className="source-info">
          <span>{source}</span>
          <span>{new Date(publishedAt).toLocaleDateString()}</span>
        </div>
        <div className="actions">
          <button><FaShare /></button>
          <button><FaBookmark /></button>
          <button><FaChartLine /></button>
        </div>
      </div>
    </div>
  );
};

export default NewsCard;
