import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState('technology'); // Default topic

  useEffect(() => {
    const fetchNews = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:5001/api/scrape-news`);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Fetched articles:', data);
        setNews(data);
      } catch (error) {
        console.error('Error fetching news:', error);
        setNews([]); // Set empty array on error
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [selectedTopic]);

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News</title>
        <meta name="description" content="Stay updated with the latest news." />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>AI News Aggregator</h1>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : news.length > 0 ? (
              <NewsGrid articles={news} />
            ) : (
              <div className="no-articles">No articles available. Please try again later.</div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
