import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState('Politics'); // Default sub-topic
  const [language, setLanguage] = useState('en');
  const [searchTerm, setSearchTerm] = useState(''); // State for search term

  useEffect(() => {
    const getNews = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:5000/api/newsapi?topic=${selectedTopic}&language=${language}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const articles = await response.json();
        console.log('Fetched articles:', articles);
        setNews(articles); // Directly set the fetched articles
      } catch (error) {
        if (error.message.includes('429')) {
          console.error('Too many requests. Please try again later.');
          alert('Too many requests. Please try again later.'); // Show user-friendly message
        } else {
          console.error('Error fetching news:', error);
        }
      } finally {
        setLoading(false);
      }
    };

    getNews();
  }, [selectedTopic, language]); // Fetch news when selectedTopic changes

  // Fetch articles based on search term
  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent default form submission
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/newsapi?search=${searchTerm}&language=${language}`);
      const articles = await response.json();
      console.log('Fetched articles based on search:', articles);
      setNews(articles); // Set the fetched articles based on search
    } catch (error) {
      console.error('Error fetching articles based on search:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch generated article
  const fetchGeneratedArticle = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/generate-article');
      const article = await response.json();
      setNews((prevNews) => [...prevNews, article]); // Add the generated article to the news state
    } catch (error) {
      console.error('Error fetching generated article:', error);
    }
  };

  // Call fetchGeneratedArticle when the component mounts
  useEffect(() => {
    fetchGeneratedArticle();
  }, []);

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News on Technology and Innovation</title>
        <meta name="description" content="Stay updated with the latest news on technology, AI, and innovation, curated and summarized by our AI-powered news aggregator." />
        <meta name="keywords" content="AI news, technology news, innovation, artificial intelligence, news aggregator" />
        <meta property="og:title" content="AI News Aggregator" />
        <meta property="og:description" content="AI-powered news curation and summarization" />
        <meta property="og:type" content="website" />
        <link rel="canonical" href="https://your-domain.com" />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic} // Pass the selected topic to the sidebar
            selectedTopic={selectedTopic} // Pass the current selected topic
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>AI News Aggregator</h1>
              <select
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
                className="language-select"
              >
                <option value="en">English</option>
                <option value="hi">हिंदी</option>
              </select>
            </div>

            {/* Search Input Field */}
            <form onSubmit={handleSearch} className="search-form">
              <input
                type="text"
                placeholder="Search for articles..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <button type="submit" className="search-button">Search</button>
            </form>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : (
              <NewsGrid articles={news} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
