import React, { useState } from 'react';
import { FaShare, FaChartLine, FaLanguage, FaEye } from 'react-icons/fa';
import './NewsCard.css';

const NewsCard = ({ article }) => {
  const [expanded, setExpanded] = useState(false);
  const [language, setLanguage] = useState('en');

  const {
    title,
    summary,
    category,
    images,
    translations,
    analytics,
    source,
    publishedAt,
    readingTime
  } = article;

  const getContent = () => {
    if (language === 'hi' && translations?.hi) {
      return {
        title: translations.hi.title,
        summary: translations.hi.summary
      };
    }
    return { title, summary };
  };

  const content = getContent();
  const mainImage = images?.find(img => img.type === 'photo');

  return (
    <div className={`news-card ${expanded ? 'expanded' : ''}`}>
      {mainImage && (
        <div className="card-image">
          <img src={mainImage.url} alt={mainImage.alt} />
        </div>
      )}

      <div className="card-header">
        <span className="category-tag">{category}</span>
        <div className="analytics-badge">
          <FaEye /> {analytics.views}
        </div>
      </div>

      <h2 className="title">{content.title}</h2>

      <div className="summary">
        <p>{expanded ? content.summary : `${content.summary.slice(0, 150)}...`}</p>
        <button 
          className="expand-btn"
          onClick={() => setExpanded(!expanded)}
        >
          {expanded ? 'Show Less' : 'Read More'}
        </button>
      </div>

      {expanded && images?.some(img => img.type !== 'photo') && (
        <div className="visualizations">
          {images
            .filter(img => img.type !== 'photo')
            .map((img, index) => (
              <div key={index} className="visualization">
                <img src={img.url} alt={img.alt} />
                <span className="viz-type">{img.type}</span>
              </div>
            ))}
        </div>
      )}

      <div className="card-footer">
        <div className="source-info">
          <span>{source.name}</span>
          <span>{new Date(publishedAt).toLocaleDateString()}</span>
          <span>{readingTime} min read</span>
        </div>
        <div className="actions">
          <button onClick={() => setLanguage(lang => lang === 'en' ? 'hi' : 'en')}>
            <FaLanguage />
          </button>
          <button className="share-btn">
            <FaShare />
            <span className="share-count">{analytics.shares.total}</span>
          </button>
          <button>
            <FaChartLine />
          </button>
        </div>
      </div>
    </div>
  );
};

export default NewsCard;
