import React from 'react';
import { FaGlobe, FaMapMarkerAlt } from 'react-icons/fa';
import './TopicSidebar.css';

const TopicSidebar = ({ 
  topics, 
  selectedTopic, 
  selectedSubTopic,
  onTopicSelect,
  onSubTopicSelect 
}) => {
  return (
    <aside className="topic-sidebar">
      <h2>Topics</h2>
      <nav className="topic-nav">
        {Object.entries(topics).map(([key, topic]) => (
          <div key={key} className="topic-section">
            <button
              className={`topic-btn ${selectedTopic === key ? 'active' : ''}`}
              onClick={() => {
                onTopicSelect(key);
                onSubTopicSelect(null);
              }}
            >
              {key === 'global' ? <FaGlobe /> : <FaMapMarkerAlt />}
              <span>{topic.name}</span>
            </button>
            
            {selectedTopic === key && (
              <div className="subtopics">
                {topic.subtopics.map(subtopic => (
                  <button
                    key={subtopic}
                    className={`subtopic-btn ${selectedSubTopic === subtopic ? 'active' : ''}`}
                    onClick={() => onSubTopicSelect(subtopic)}
                  >
                    {subtopic.charAt(0).toUpperCase() + subtopic.slice(1)}
                  </button>
                ))}
              </div>
            )}
          </div>
        ))}
      </nav>
    </aside>
  );
};

export default TopicSidebar;
