import React, { useState } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [selectedTopic, setSelectedTopic] = useState('Technology');

  // Hardcoded news data
  const newsData = {
    'Technology': [
      {
        title: "New AI Breakthrough in Machine Learning",
        description: "Researchers have developed a new algorithm that improves deep learning efficiency by 50%.",
        url: "#",
        source: { name: "Tech News" }
      },
      {
        title: "Latest iPhone Features Leaked",
        description: "Upcoming iPhone model to include revolutionary battery technology and enhanced AI capabilities.",
        url: "#",
        source: { name: "Apple Insider" }
      },
      {
        title: "SpaceX Announces New Mission",
        description: "SpaceX reveals plans for its next-generation spacecraft designed for Mars colonization.",
        url: "#",
        source: { name: "Space News" }
      }
    ],
    'Business': [
      {
        title: "Global Markets Hit New High",
        description: "Stock markets worldwide reach record levels amid strong economic data.",
        url: "#",
        source: { name: "Financial Times" }
      },
      {
        title: "Startup Raises $100M in Funding",
        description: "AI-driven sustainability startup secures major investment for global expansion.",
        url: "#",
        source: { name: "Business Weekly" }
      }
    ],
    'Health': [
      {
        title: "Breakthrough in Cancer Research",
        description: "Scientists discover new treatment method with promising results in clinical trials.",
        url: "#",
        source: { name: "Health News" }
      },
      {
        title: "Mental Health Innovation",
        description: "New app uses AI to provide personalized mental health support.",
        url: "#",
        source: { name: "Healthcare Tech" }
      }
    ],
    'Science': [
      {
        title: "Quantum Computing Milestone",
        description: "Researchers achieve quantum supremacy in new experiment.",
        url: "#",
        source: { name: "Science Daily" }
      },
      {
        title: "New Species Discovered",
        description: "Biologists find previously unknown species in Amazon rainforest.",
        url: "#",
        source: { name: "Nature" }
      }
    ]
  };

  // Get news for selected topic
  const getNewsForTopic = (topic) => {
    return newsData[topic] || newsData['Technology']; // Default to Technology if topic not found
  };

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News</title>
        <meta name="description" content="Stay updated with the latest news." />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>AI News Aggregator</h1>
              <h2>{selectedTopic} News</h2>
            </div>

            <NewsGrid articles={getNewsForTopic(selectedTopic)} />
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
