import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState('general'); // Default topic
  const [language, setLanguage] = useState('en');
  const API_KEY = process.env.REACT_APP_NEWS_API_KEY; // Ensure this is set correctly in your .env file

  useEffect(() => {
    const getNews = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://newsapi.org/v2/top-headlines?category=${selectedTopic}&language=${language}&apiKey=${API_KEY}`);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Fetched articles:', data.articles);
        setNews(data.articles); // Set the fetched articles
      } catch (error) {
        console.error('Error fetching news:', error);
      } finally {
        setLoading(false);
      }
    };

    getNews();
  }, [selectedTopic, language]); // Fetch news when selectedTopic or language changes

  // Fetch generated article
  const fetchGeneratedArticle = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/generate-article');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const article = await response.json();
      setNews((prevNews) => [...prevNews, article]); // Add the generated article to the news state
    } catch (error) {
      console.error('Error fetching generated article:', error);
    }
  };

  // Call fetchGeneratedArticle when the component mounts
  useEffect(() => {
    fetchGeneratedArticle();
  }, []);

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News</title>
        <meta name="description" content="Stay updated with the latest news." />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic} // Pass the selected topic to the sidebar
            selectedTopic={selectedTopic} // Pass the current selected topic
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>AI News Aggregator</h1>
              <select
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
                className="language-select"
              >
                <option value="en">English</option>
                <option value="hi">हिंदी</option>
              </select>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : (
              <NewsGrid articles={news} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
