import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

export const fetchArticles = async (params) => {
  try {
    const response = await api.get('/articles', { params });
    return response.data;
  } catch (error) {
    console.error('Error fetching articles:', error?.response?.data || error.message);
    throw new Error(error?.response?.data?.message || 'Failed to fetch articles');
  }
};


export const trackArticleView = async (articleId) => {
  try {
    await api.post(`/articles/${articleId}/view`);
  } catch (error) {
    console.error('Error tracking article view:', error);
  }
};

export const shareArticle = async (articleId, platform) => {
  try {
    await api.post(`/articles/${articleId}/share`, { platform });
  } catch (error) {
    console.error('Error tracking share:', error);
  }
};


export default api;
