import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState('technology');

  // API key directly in component (not recommended for production)
  const API_KEY = 'ea90788d06fe4d1c980f73522c31bab7';

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const url = `https://newsapi.org/v2/everything?q=${selectedTopic}&language=en&sortBy=publishedAt&apiKey=${API_KEY}`;
        const response = await fetch(url);
        const data = await response.json();

        if (data.status === 'ok') {
          setNews(data.articles);
        } else {
          console.error('Error in API response:', data.message);
        }
      } catch (error) {
        console.error('Failed to fetch news:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [selectedTopic]);

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News</title>
        <meta name="description" content="Stay updated with the latest news." />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>Latest {selectedTopic} News</h1>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : (
              <NewsGrid articles={news} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
