import React from 'react';
import './TopicSidebar.css';

// These are the valid categories for News API
const topics = ['general', 'business', 'technology', 'entertainment', 'health', 'science', 'sports'];

const TopicSidebar = ({ onTopicSelect, selectedTopic }) => {
  return (
    <div className="topic-sidebar">
      <h3>Categories</h3>
      <div className="topic-list">
        {topics.map((topic) => (
          <div
            key={topic}
            className={`topic-item ${selectedTopic === topic ? 'active' : ''}`}
            onClick={() => onTopicSelect(topic)}
          >
            {topic.charAt(0).toUpperCase() + topic.slice(1)}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TopicSidebar;
