import React from 'react';
import './TopicSidebar.css';

const topics = {
  'Uttar Pradesh': {
    name: 'Uttar Pradesh News',
    subtopics: ['Lucknow', 'Varanasi', 'Agra', 'Kanpur', 'Ghaziabad'],
  },
  'Maharashtra': {
    name: 'Maharashtra News',
    subtopics: ['Mumbai', 'Pune', 'Nagpur', 'Nashik', 'Thane'],
  },
  'Delhi': {
    name: 'Delhi News',
    subtopics: ['Central Delhi', 'South Delhi', 'North Delhi', 'East Delhi', 'West Delhi'],
  },
  'Karnataka': {
    name: 'Karnataka News',
    subtopics: ['Bangalore', 'Mysore', 'Mangalore', 'Hubli', 'Dharwad'],
  },
  'Tamil Nadu': {
    name: 'Tamil Nadu News',
    subtopics: ['Chennai', 'Coimbatore', 'Madurai', 'Tiruchirappalli', 'Salem'],
  },
};

const TopicSidebar = ({ onTopicSelect, selectedTopic }) => {
  return (
    <div className="topic-sidebar">
      <h3>Topics</h3>
      {Object.entries(topics).map(([key, topic]) => (
        <div key={key} className="topic-section">
          <h4>{topic.name}</h4>
          {topic.subtopics.map((subtopic) => (
            <div
              key={subtopic}
              className={`topic-header ${selectedTopic === subtopic ? 'active' : ''}`}
              onClick={() => onTopicSelect(subtopic)}
            >
              {subtopic}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default TopicSidebar;
