import React from 'react';
import NewsCard from './NewsCard';
import './NewsGrid.css';

const NewsGrid = ({ articles }) => {
  // Separate AI and regular articles
  const aiArticles = articles.filter(article => article.isAIGenerated);
  const regularArticles = articles.filter(article => !article.isAIGenerated);

  return (
    <div className="news-container">
      {aiArticles.length > 0 && (
        <div className="section">
          <h2 className="section-title">AI Generated Insights</h2>
          <div className="news-grid">
            {aiArticles.map(article => (
              <NewsCard key={article._id} article={article} />
            ))}
          </div>
        </div>
      )}

      <div className="section">
        <h2 className="section-title">Latest News</h2>
        <div className="news-grid">
          {regularArticles.map(article => (
            <NewsCard key={article._id} article={article} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default NewsGrid;
