import React, { useState } from 'react';
import './TopicSidebar.css';

const topics = {
  global: {
    name: 'Global News',
    subtopics: ['Politics', 'Technology', 'Economy']
  },
  india: {
    name: 'Indian News',
    subtopics: ['Uttar Pradesh', 'Maharashtra', 'Delhi']
  },
  // Add more topics and subtopics as needed
};

const TopicSidebar = ({ onTopicSelect, selectedTopic }) => {
  const [selectedSubtopic, setSelectedSubtopic] = useState(null);

  const handleTopicSelect = (topic) => {
    onTopicSelect(topic);
    setSelectedSubtopic(null); // Reset subtopic when changing main topic
  };

  const handleSubtopicSelect = (subtopic) => {
    setSelectedSubtopic(subtopic);
    onTopicSelect(selectedTopic, subtopic); // Pass both topic and subtopic
  };

  return (
    <div className="topic-sidebar">
      <h2>Select a Topic</h2>
      <ul>
        {Object.entries(topics).map(([key, topic]) => (
          <li key={key} className={selectedTopic === key ? 'active' : ''} onClick={() => handleTopicSelect(key)}>
            {topic.name}
          </li>
        ))}
      </ul>
      {selectedTopic && (
        <div className="subtopics">
          <h3>Subtopics</h3>
          <ul>
            {topics[selectedTopic].subtopics.map((subtopic) => (
              <li key={subtopic} onClick={() => handleSubtopicSelect(subtopic)}>
                {subtopic}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TopicSidebar;
