import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import NewsGrid from './NewsGrid';
import TopicSidebar from './TopicSidebar';
import TranslationService from '../../server/src/services/translationService'; // Adjust the path as necessary
import './Dashboard.css';

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTopic, setSelectedTopic] = useState('general');
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const getNews = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:5000/api/newsapi?category=${selectedTopic}&language=${language}`);
        const articles = await response.json();
        console.log('Fetched articles:', articles);
        const articlesWithTranslations = await Promise.all(
          articles.map(translateContent)
        );
        setNews(articlesWithTranslations);
      } catch (error) {
        console.error('Error fetching news:', error);
      } finally {
        setLoading(false);
      }
    };

    getNews();
  }, [selectedTopic, language]);

  // Fetch generated article
  const fetchGeneratedArticle = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/generate-article');
      const article = await response.json();
      setNews((prevNews) => [...prevNews, article]); // Add the generated article to the news state
    } catch (error) {
      console.error('Error fetching generated article:', error);
    }
  };

  // Call fetchGeneratedArticle when the component mounts
  useEffect(() => {
    fetchGeneratedArticle();
  }, []);

  const translateContent = async (article) => {
    try {
      const translatedTitle = await TranslationService.translateText(article.title, 'hi');
      const translatedSummary = await TranslationService.translateText(article.summary, 'hi');

      return {
        ...article,
        translations: {
          hi: {
            title: translatedTitle,
            summary: translatedSummary,
          },
        },
      };
    } catch (error) {
      console.error('Error translating content:', error);
      return article; // Return the original article if translation fails
    }
  };

  return (
    <>
      <Helmet>
        <title>AI News Aggregator - Latest News on Technology and Innovation</title>
        <meta name="description" content="Stay updated with the latest news on technology, AI, and innovation, curated and summarized by our AI-powered news aggregator." />
        <meta name="keywords" content="AI news, technology news, innovation, artificial intelligence, news aggregator" />
        <meta property="og:title" content="AI News Aggregator" />
        <meta property="og:description" content="AI-powered news curation and summarization" />
        <meta property="og:type" content="website" />
        <link rel="canonical" href="https://your-domain.com" />
      </Helmet>

      <div className="dashboard">
        <div className="dashboard-layout">
          <TopicSidebar 
            onTopicSelect={setSelectedTopic}
            selectedTopic={selectedTopic}
          />

          <div className="main-content">
            <div className="dashboard-header">
              <h1>AI News Aggregator</h1>
              <select
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
                className="language-select"
              >
                <option value="en">English</option>
                <option value="hi">हिंदी</option>
              </select>
            </div>

            {loading ? (
              <div className="loader">Loading...</div>
            ) : (
              <NewsGrid articles={news} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
