import React, { useState } from 'react';
import { FaShare, FaBookmark, FaRobot } from 'react-icons/fa';
import './NewsCard.css';

const NewsCard = ({ article }) => {
  const [expanded, setExpanded] = useState(false);

  const {
    title,
    summary,
    source,
    publishedAt,
    translations,
    language,
    isAIGenerated,
    imageUrl
  } = article;

  const getContent = () => {
    if (language === 'hi' && translations?.hi) {
      return {
        title: translations.hi.title,
        summary: translations.hi.summary
      };
    }
    return { title, summary };
  };

  const displayContent = getContent();

  return (
    <div className={`news-card ${expanded ? 'expanded' : ''} ${isAIGenerated ? 'ai-generated' : ''}`}>
      {isAIGenerated && (
        <div className="ai-badge">
          <FaRobot />
          <span>AI Generated</span>
        </div>
      )}

      <div className="news-image">
        {imageUrl ? (
          <img src={imageUrl} alt={title} />
        ) : (
          <div className="placeholder-image">No Image</div>
        )}
      </div>

      <div className="news-content">
        <h3>{displayContent.title}</h3>
        <p className="summary">
          {expanded ? displayContent.summary : `${displayContent.summary.slice(0, 150)}...`}
          <button 
            className="expand-btn"
            onClick={() => setExpanded(!expanded)}
          >
            {expanded ? 'Show Less' : 'Read More'}
          </button>
        </p>

        <div className="metadata">
          <span className="source">{source}</span>
          <span className="date">
            {new Date(publishedAt).toLocaleDateString()}
          </span>
        </div>

        <div className="actions">
          <button className="share-btn">
            <FaShare />
            Share
          </button>
          <button className="save-btn">
            <FaBookmark />
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default NewsCard;
