import React from 'react';
import { FaGlobe, FaCity } from 'react-icons/fa';
import './TopicSidebar.css';

const TopicSidebar = ({ onTopicSelect, selectedTopic }) => {
  const topics = {
    global: {
      name: 'International News',
      icon: <FaGlobe />,
      subtopics: ['Politics', 'Technology', 'Economy']
    },
    india: {
      name: 'Indian News',
      icon: <FaCity />,
      subtopics: ['Uttar Pradesh', 'Lucknow', 'Local Business']
    }
  };

  return (
    <div className="topic-sidebar">
      {Object.entries(topics).map(([key, topic]) => (
        <div key={key} className="topic-section">
          <div 
            className={`topic-header ${selectedTopic === key ? 'active' : ''}`}
            onClick={() => onTopicSelect(key)}
          >
            {topic.icon}
            <span>{topic.name}</span>
          </div>
          <div className="subtopics">
            {topic.subtopics.map(subtopic => (
              <button 
                key={subtopic}
                className="subtopic-btn"
                onClick={() => onTopicSelect(`${key}-${subtopic.toLowerCase()}`)}
              >
                {subtopic}
              </button>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default TopicSidebar;
